import React, { useState } from "react";
import "../Css/Hero.css";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";


const Hero = () => {
  const [text, settext] = useState("");

  const convertCap = () => {
    const newtext = text.toUpperCase();
    settext(newtext);
  };
  const change = (event) => {
    settext(event.target.value);
  };
  const convertlow = () => {
    const newtext = text.toLowerCase();
    settext(newtext);
  };

  const generatePdf=()=>{
    const input = document.getElementById('preview');
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/png');
        const pdf = new jsPDF('p', 'pt', 'a4', false);
        pdf.addImage(imgData, 'PNG', 0, 0, 600, 0, undefined, false);
        // pdf.output('dataurlnewwindow');
        pdf.save("download.pdf");
      });
   
  }

  const searchElement=()=>{

  }
  return (
    <>
      <div className="hero-section">
        <h1>Text Analyzer</h1>
        <div className="container">
          {/* <label className="label">Typing Expert go here !!</label> */}
          <textarea
            className="text-area"
            placeholder="Enter the text here"
            onChange={change}
            value={text}
          ></textarea>
        </div>
        <div className="btn-container">
          <button onClick={convertCap}>Convert Capital</button>
          <button onClick={convertlow}>Convert smaller</button>
        </div>

        <p className="words">
          {text.split(" ").length} words and {text.length} character
        </p>
        <p className="sec">{0.008 * text.split(" ").length} min to read</p>
        <h2>Preview</h2>
        <p className="preview" id="preview">{text}</p>
        <button className="downloadBtn" onClick={generatePdf}>Download PDF</button>
      </div>
    </>
  );
};

export default Hero;
