import React, { useState } from "react";
import "../Css/Hero.css";
import html2pdf from "html2pdf.js";

const Hero = () => {
  const [text, settext] = useState("");
  const convertCap = () => {
    const newtext = text.toUpperCase();
    settext(newtext);
  };
  const change = (event) => {
    settext(event.target.value);
  };
  const convertlow = () => {
    const newtext = text.toLowerCase();
    settext(newtext);
  };

  const generatePdf=()=>{
    const element=document.getElementsByClassName("preview");
    html2pdf().from(element).save();
  }
  return (
    <>
      <div className="hero-section">
        <h1>Check your typing skills</h1>
        <div className="container">
          {/* <label className="label">Typing Expert go here !!</label> */}
          <textarea
            className="text-area"
            placeholder="Enter the text here"
            onChange={change}
            value={text}
          ></textarea>
        </div>
        <div className="btn-container">
          <button onClick={convertCap}>Convert Capital</button>
          <button onClick={convertlow}>Convert smaller</button>
        </div>

        <p className="words">
          {text.split(" ").length} words and {text.length} character
        </p>
        {/* <p className="sec">{0.008 * text.split(" ").length} sec to read</p> */}
        <h2>Preview</h2>
        <p className="preview">{text}</p>
        <button onClick={generatePdf}>Download PDF</button>
      </div>
    </>
  );
};

export default Hero;
